Scripts.OnLoad:
this:SetFrameLevel("1");
this:SetBackdropBorderColor(
	TOOLTIP_DEFAULT_COLOR.r,
	TOOLTIP_DEFAULT_COLOR.g,
	TOOLTIP_DEFAULT_COLOR.b);
this:SetBackdropColor(
	TOOLTIP_DEFAULT_BACKGROUND_COLOR.r,
	TOOLTIP_DEFAULT_BACKGROUND_COLOR.g,
	TOOLTIP_DEFAULT_BACKGROUND_COLOR.b);

Scripts.OnClick:
if (this:GetChecked()) then
	AutoBar_Config[AutoBar_Player].hidekeys = 1;
else
	AutoBar_Config[AutoBar_Player].hidekeys = 0;
end

if (this:GetChecked()) then
	AutoBar_Config[AutoBar_Player].selfcast = 1;
else
	AutoBar_Config[AutoBar_Player].selfcast = 0;
end

if (this:GetChecked()) then
	AutoBar_Config[AutoBar_Player].donotskip = 1;
else
	AutoBar_Config[AutoBar_Player].donotskip = 0;
end

if (this:GetChecked()) then
	AutoBar_Config[AutoBar_Player].hidecount = 1;
else
	AutoBar_Config[AutoBar_Player].hidecount = nil;
end

								if (this:GetChecked()) then
									AutoBar_Config[AutoBar_Player].microaspect = 1;

								else
									AutoBar_Config[AutoBar_Player].microaspect = nil;
								end
								AutoBar_Config_Tab_OnClick();
								AutoBar_Init();
